import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, useNavigate } from 'react-router-dom';
import TreeView from './components/TreeView';
import ItemDetails from './components/ItemDetails';
import LoginPage from './components/LoginPage';
import SignUpPage from './components/SignUpPage';

function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [selectedItemId, setSelectedItemId] = useState(null);
  const navigate = useNavigate();  // Use the useNavigate hook

  const handleLogin = () => setLoggedIn(true);

  const handleSignUp = () => {
    setLoggedIn(true);  // Set loggedIn to true after sign-up
    navigate('/tree');  // Redirect to the tree view after sign-up
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Navigate replace to="/login" />} />
        <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />
        <Route path="/signup" element={<SignUpPage onSignUp={handleSignUp} />} />
        {loggedIn && (
          <Route
            path="/tree"
            element={
              <div className="container">
                <TreeView onItemSelected={setSelectedItemId} />
                <ItemDetails selectedItemId={selectedItemId} />
              </div>
            }
          />
        )}
      </Routes>
    </Router>
  );
}

// Note: Use this wrapper component to call the useNavigate hook correctly
function AppWrapper() {
  return (
    <Router>
      <App />
    </Router>
  );
}

export default AppWrapper;
