import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TreeView = ({ onItemSelected }) => {
  const [godowns, setGodowns] = useState([]);  // State to store godowns
  const [expandedNodes, setExpandedNodes] = useState({});  // To track expanded/collapsed state

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/godowns/')
      .then(response => {
        setGodowns(buildTree(response.data));
      })
      .catch(error => {
        console.error("Error fetching godowns:", error);
      });
  }, []);

  const buildTree = (data) => {
    const godownMap = {};
    const rootGodowns = [];

    data.forEach(godown => {
      godown.children = [];
      godownMap[godown.id] = godown;
    });

    data.forEach(godown => {
      if (godown.parent_godown) {
        godownMap[godown.parent_godown].children.push(godown);
      } else {
        rootGodowns.push(godown);
      }
    });

    return rootGodowns;
  };

  const toggleExpand = (id) => {
    setExpandedNodes(prevState => ({
      ...prevState,
      [id]: !prevState[id]
    }));
  };

  const handleItemClick = (itemId) => {
    onItemSelected(itemId);  // Notify the parent component when an item is selected
  };

  const renderTree = (locations) => {
    return locations.map(location => (
      <li key={location.id}>
        <span onClick={() => toggleExpand(location.id)}>
          {expandedNodes[location.id] ? '[-]' : '[+]'} {location.name}
        </span>
        {expandedNodes[location.id] && location.children.length > 0 && (
          <ul>
            {renderTree(location.children)}
          </ul>
        )}
        {expandedNodes[location.id] && (
          <ul>
            {/* Replace this with actual API call to get items for this godown */}
            <li onClick={() => handleItemClick(location.id)}>Item for {location.name}</li>
          </ul>
        )}
      </li>
    ));
  };

  return (
    <div className="tree-view">
      <ul>
        {godowns.length > 0 ? renderTree(godowns) : <li>No locations available.</li>}
      </ul>
    </div>
  );
};

export default TreeView;
