// src/components/TreeView.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles.css'; // Ensure you import your styles

const TreeView = () => {
    const [godowns, setGodowns] = useState([]);
    const [items, setItems] = useState([]);
    const [selectedItem, setSelectedItem] = useState(null);
    const [expandedGodowns, setExpandedGodowns] = useState(new Set());

    useEffect(() => {
        // Fetch godowns
        axios.get('http://127.0.0.1:8000/api/godowns/')
            .then(response => {
                console.log("Godowns: ", response.data);
                setGodowns(response.data);
            })
            .catch(error => {
                console.error("Error fetching godowns: ", error);
            });

        // Fetch items
        axios.get('http://127.0.0.1:8000/api/items/')
            .then(response => {
                console.log("Items: ", response.data);
                setItems(response.data);
            })
            .catch(error => {
                console.error("Error fetching items: ", error);
            });
    }, []);

    const handleExpandToggle = (godownId) => {
        const newExpandedGodowns = new Set(expandedGodowns);
        if (newExpandedGodowns.has(godownId)) {
            newExpandedGodowns.delete(godownId);
        } else {
            newExpandedGodowns.add(godownId);
        }
        setExpandedGodowns(newExpandedGodowns);
    };

    const handleItemClick = (itemId) => {
        const selected = items.find(item => item.item_id === itemId);
        setSelectedItem(selected);
    };

    const renderItems = (godownId) => {
        return items
            .filter(item => item.godown === godownId) // Filter items belonging to the godown
            .map(item => (
                <button key={item.item_id} className="item-button" onClick={() => handleItemClick(item.item_id)}>
                    {item.name}
                </button>
            ));
    };

    const renderTree = (locations) => {
        return locations.map(location => (
            <div key={location.id}>
                <div className="godown-row">
                    <button className="expand-button" onClick={() => handleExpandToggle(location.id)}>
                        {expandedGodowns.has(location.id) ? '-' : '+'}
                    </button>
                    <span>{location.name}</span>
                </div>
                {expandedGodowns.has(location.id) && (
                    <div className="item-list">
                        {renderItems(location.id)}
                    </div>
                )}
            </div>
        ));
    };

    return (
        <div className="tree-view-container">
            <div className="sidebar">
                <h1>Godown Tree View</h1>
                <ul>
                    {godowns.length > 0 ? renderTree(godowns) : <li>No locations available.</li>}
                </ul>
            </div>
            <div className="item-details">
                {selectedItem ? (
                    <>
                        <h3>{selectedItem.name}</h3>
                        <p>Category: {selectedItem.category}</p>
                        <p>Price: ${selectedItem.price}</p>
                        <p>Status: {selectedItem.status}</p>
                        <img src={selectedItem.image_url} alt={selectedItem.name} />
                    </>
                ) : (
                    <p>Select an item to see the details.</p>
                )}
            </div>
        </div>
    );
};

export default TreeView;
