// src/components/TreeView.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TreeView = () => {
    const [godowns, setGodowns] = useState([]);
    const [selectedItem, setSelectedItem] = useState(null);
    const [expandedGodowns, setExpandedGodowns] = useState(new Set()); // To track expanded godowns

    useEffect(() => {
        axios.get('http://127.0.0.1:8000/api/godowns/')
            .then(response => {
                console.log(response.data);
                setGodowns(response.data);
            })
            .catch(error => {
                console.error("Error fetching godowns: ", error);
            });
    }, []);

    const handleExpandToggle = (godownId) => {
        const newExpandedGodowns = new Set(expandedGodowns);
        if (newExpandedGodowns.has(godownId)) {
            newExpandedGodowns.delete(godownId);
        } else {
            newExpandedGodowns.add(godownId);
        }
        setExpandedGodowns(newExpandedGodowns);
    };

    const handleItemClick = (itemId) => {
        axios.get(`http://127.0.0.1:8000/api/items/${itemId}/`)
            .then(response => {
                setSelectedItem(response.data);
            })
            .catch(error => {
                console.error("Error fetching item: ", error);
            });
    };

    const renderItems = (godownId) => {
        // Fetch items for the given godownId (this could be from a stored items state or an API call)
        return godowns
            .filter(godown => godown.id === godownId) // Replace with actual filtering logic based on items data
            .map(item => (
                <button key={item.item_id} className="item-button" onClick={() => handleItemClick(item.item_id)}>
                    {item.name}
                </button>
            ));
    };

    const renderTree = (locations) => {
        return locations.map(location => (
            <div key={location.id}>
                <div className="godown-row">
                    <button className="expand-button" onClick={() => handleExpandToggle(location.id)}>
                        {expandedGodowns.has(location.id) ? '-' : '+'}
                    </button>
                    <span>{location.name}</span>
                </div>
                {expandedGodowns.has(location.id) && (
                    <div className="item-list">
                        {renderItems(location.id)}
                    </div>
                )}
            </div>
        ));
    };

    return (
        <div className="tree-view-container" style={{ display: 'flex', padding: '20px' }}>
            <div className="sidebar" style={{ width: '300px', borderRight: '1px solid #444', backgroundColor: '#222', padding: '10px' }}>
                <h1 style={{ fontSize: '2rem' }}>Godown Tree View</h1>
                <ul style={{ listStyle: 'none', padding: '0' }}>
                    {godowns.length > 0 ? renderTree(godowns) : <li>No locations available.</li>}
                </ul>
            </div>
            <div className="item-details" style={{ marginLeft: '20px', padding: '20px', backgroundColor: '#333', borderRadius: '5px', flex: '1' }}>
                {selectedItem ? (
                    <>
                        <h3 style={{ fontSize: '1.5rem' }}>{selectedItem.name}</h3>
                        <p>Category: {selectedItem.category}</p>
                        <p>Price: ${selectedItem.price}</p>
                        <p>Status: {selectedItem.status}</p>
                        <img src={selectedItem.image_url} alt={selectedItem.name} style={{ maxWidth: '100%' }} />
                    </>
                ) : (
                    <p>Select an item to see the details.</p>
                )}
            </div>
        </div>
    );
};

export default TreeView;
