import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles.css'; // Import your CSS file

const TreeView = () => {
    const [godowns, setGodowns] = useState([]);
    const [items, setItems] = useState([]);
    const [selectedGodown, setSelectedGodown] = useState(null);
    const [selectedItem, setSelectedItem] = useState(null);

    useEffect(() => {
        // Fetch godowns
        axios.get('http://127.0.0.1:8000/api/godowns/')
            .then(response => {
                setGodowns(response.data);
            })
            .catch(error => {
                console.error("There was an error fetching the godowns!", error);
            });
    }, []);

    const handleGodownClick = (godownId) => {
        // Fetch items for the selected godown
        axios.get(`http://127.0.0.1:8000/api/items/?godown=${godownId}`) // Adjusted if you have a query to filter
            .then(response => {
                setItems(response.data);
                setSelectedGodown(godownId); // Store the selected godown ID
                setSelectedItem(null); // Reset selected item when changing godown
            })
            .catch(error => {
                console.error("There was an error fetching the items!", error);
            });
    };

    const handleItemClick = (itemId) => {
        // Fetch item details
        axios.get(`http://127.0.0.1:8000/api/items/${itemId}/`)
            .then(response => {
                setSelectedItem(response.data);
            })
            .catch(error => {
                console.error("There was an error fetching the item!", error);
            });
    };

    return (
        <div className="tree-view-container">
            <div className="sidebar">
                <h2>Godowns</h2>
                <ul>
                    {godowns.length > 0 ? (
                        godowns.map(godown => (
                            <li key={godown.id} onClick={() => handleGodownClick(godown.id)}>
                                {godown.name}
                            </li>
                        ))
                    ) : (
                        <li>No locations available.</li>
                    )}
                </ul>
            </div>

            <div className="main-display">
                <h1>Items</h1>
                {selectedGodown ? (
                    <>
                        <h2>Items in {godowns.find(g => g.id === selectedGodown)?.name}</h2>
                        <div className="item-list">
                            {items.length > 0 ? (
                                items.map(item => (
                                    <button key={item.item_id} className="item-button" onClick={() => handleItemClick(item.item_id)}>
                                        {item.name}
                                    </button>
                                ))
                            ) : (
                                <p>No items available in this godown.</p>
                            )}
                        </div>
                    </>
                ) : (
                    <p>Please select a godown to view items.</p>
                )}

                {/* Display selected item details */}
                {selectedItem && (
                    <div className="item-details">
                        <h3>{selectedItem.name}</h3>
                        <p>Category: {selectedItem.category}</p>
                        <p>Price: ${selectedItem.price}</p>
                        <p>Status: {selectedItem.status}</p>
                        <p>Brand: {selectedItem.brand}</p>
                        <img src={selectedItem.image_url} alt={selectedItem.name} />
                    </div>
                )}
            </div>
        </div>
    );
};

export default TreeView;
