import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TreeView = () => {
  const [godowns, setGodowns] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/godowns/')
      .then(response => {
        setGodowns(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the godowns!", error);
      });
  }, []);

  const handleItemClick = (itemId) => {
    axios.get(`http://127.0.0.1:8000/api/items/${itemId}/`)
      .then(response => {
        setSelectedItem(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the item!", error);
      });
  };

  const renderTree = (locations) => {
    return locations.map(location => (
      <div key={location.id}>
        <button className="godown-button" onClick={() => toggleExpand(location.id)}>
          {location.name}
          <span className="expand-button">{location.isExpanded ? '-' : '+'}</span>
        </button>
        {location.isExpanded && (
          <>
            <div className="subgodowns">
              {renderTree(getSubGodowns(location.id))}
            </div>
            <div className="items">
              {getItemsForGodown(location.id).map(item => (
                <button key={item.item_id} className="item-button" onClick={() => handleItemClick(item.item_id)}>
                  {item.name}
                </button>
              ))}
            </div>
          </>
        )}
      </div>
    ));
  };

  const toggleExpand = (id) => {
    setGodowns(godowns.map(godown => 
      godown.id === id ? { ...godown, isExpanded: !godown.isExpanded } : godown
    ));
  };

  const getSubGodowns = (parentId) => {
    return godowns.filter(godown => godown.parent_godown === parentId);
  };

  const getItemsForGodown = (godownId) => {
    // This function should return items associated with the given godownId
    // For demonstration, this function will be mocked up. Please replace it with your logic to fetch items.
    return []; // Replace with actual items fetching logic
  };

  return (
    <div className="tree-view-container">
      <div className="sidebar">
        <h2>Godowns</h2>
        {renderTree(godowns.filter(godown => godown.parent_godown === null))}
      </div>
      <div className="main-content">
        {selectedItem && (
          <div className="item-details">
            <h3>{selectedItem.name}</h3>
            <p>Quantity: {selectedItem.quantity}</p>
            <p>Category: {selectedItem.category}</p>
            <p>Price: ${selectedItem.price}</p>
            <p>Status: {selectedItem.status}</p>
            <p>Brand: {selectedItem.brand}</p>
            <p>Type: {selectedItem.attributes.type}</p>
            <p>Material: {selectedItem.attributes.material}</p>
            <p>Warranty Years: {selectedItem.attributes.warranty_years}</p>
            <img src={selectedItem.image_url} alt={selectedItem.name} />
          </div>
        )}
      </div>
    </div>
  );
};

export default TreeView;
