import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles.css'; // Make sure the path to your styles.css is correct

const TreeView = () => {
    const [godowns, setGodowns] = useState([]);
    const [items, setItems] = useState([]);
    const [selectedItem, setSelectedItem] = useState(null);
    const [expandedGodowns, setExpandedGodowns] = useState({});

    useEffect(() => {
        // Fetch godowns
        axios.get('http://127.0.0.1:8000/api/godowns/')
            .then(response => {
                console.log("Godowns:", response.data); // Debugging
                setGodowns(response.data);
            })
            .catch(error => {
                console.error("Error fetching godowns:", error);
            });

        // Fetch items
        axios.get('http://127.0.0.1:8000/api/items/')
            .then(response => {
                console.log("Items:", response.data); // Debugging
                setItems(response.data);
            })
            .catch(error => {
                console.error("Error fetching items:", error);
            });
    }, []);

    const handleItemClick = (itemId) => {
        axios.get(`http://127.0.0.1:8000/api/items/${itemId}/`)
            .then(response => {
                setSelectedItem(response.data);
            })
            .catch(error => {
                console.error("Error fetching the item:", error);
            });
    };

    const handleExpand = (godownId) => {
        setExpandedGodowns(prev => ({
            ...prev,
            [godownId]: !prev[godownId], // Toggle the expanded state
        }));
    };

    const renderItems = (godownId) => {
        return items
            .filter(item => item.godown === godownId) // Ensure item.godown matches godownId
            .map(item => (
                <button key={item.item_id} className="item-button" onClick={() => handleItemClick(item.item_id)}>
                    {item.name}
                </button>
            ));
    };

    const renderTree = () => {
        return godowns
            .filter(godown => godown.parent_godown === null) // Only show top-level godowns
            .map(godown => (
                <div key={godown.id}>
                    <button className="godown-button" onClick={() => handleExpand(godown.id)}>
                        {godown.name}
                    </button>
                    {expandedGodowns[godown.id] && (
                        <div className="sub-godown-list">
                            {godowns
                                .filter(subGodown => subGodown.parent_godown === godown.id) // Sub-godowns
                                .map(subGodown => (
                                    <div key={subGodown.id}>
                                        <button className="godown-button" onClick={() => handleExpand(subGodown.id)}>
                                            {subGodown.name}
                                        </button>
                                        {expandedGodowns[subGodown.id] && renderItems(subGodown.id)} {/* Show items */}
                                    </div>
                                ))}
                        </div>
                    )}
                </div>
            ));
    };

    return (
        <div className="tree-view-container">
            <h1 className="title">Godown Tree View</h1>
            <div className="sidebar">
                <h2>Godowns</h2>
                {renderTree()}
            </div>
            <div className="main-content">
                {selectedItem ? (
                    <div className="item-details">
                        <h3>{selectedItem.name}</h3>
                        <p>Category: {selectedItem.category}</p>
                        <p>Price: ${selectedItem.price}</p>
                        <p>Status: {selectedItem.status}</p>
                        <img src={selectedItem.image_url} alt={selectedItem.name} />
                    </div>
                ) : (
                    <p>Select an item to see the details.</p>
                )}
            </div>
        </div>
    );
};

export default TreeView;
