import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TreeView = () => {
  const [godowns, setGodowns] = useState([]);  // State to store godowns
  const [selectedItem, setSelectedItem] = useState(null);  // State for selected item

  // Fetch godowns on component mount
  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/godowns/')
      .then(response => {
        console.log("Godowns API Response: ", response.data);  // Log API response
        setGodowns(buildTree(response.data));  // Build and set the godown hierarchy
      })
      .catch(error => {
        console.error("Error fetching godowns:", error);
      });
  }, []);

  // Helper function to build the hierarchical tree structure
  const buildTree = (data) => {
    const godownMap = {};  // Map to hold godowns by id
    const rootGodowns = [];  // Root godowns (without parent)

    // Initialize all godowns in the map
    data.forEach(godown => {
      godown.children = [];  // Initialize children array
      godownMap[godown.id] = godown;  // Store godown by id
    });

    // Populate the tree structure
    data.forEach(godown => {
      if (godown.parent_godown) {
        godownMap[godown.parent_godown].children.push(godown);  // Add to parent's children array
      } else {
        rootGodowns.push(godown);  // Add to root if no parent
      }
    });

    return rootGodowns;  // Return the root godowns
  };

  // Fetch item details when clicking on a godown
  const handleItemClick = (itemId) => {
    axios.get(`http://127.0.0.1:8000/api/items/${itemId}/`)
      .then(response => {
        setSelectedItem(response.data);  // Set selected item in state
      })
      .catch(error => {
        console.error("Error fetching item:", error);
      });
  };

  // Recursive function to render godown tree
  const renderTree = (nodes) => {
    return nodes.map((node) => (
      <li key={node.id}>
        <span onClick={() => handleItemClick(node.id)}>{node.name}</span>
        {node.children.length > 0 && (
          <ul>{renderTree(node.children)}</ul>  // Recursively render children
        )}
      </li>
    ));
  };

  return (
    <div className="tree-view-container">
      <h1>Godown Tree View</h1>
      <ul>
        {godowns.length > 0 ? renderTree(godowns) : <li>No locations available.</li>}
      </ul>

      {/* Display selected item details */}
      {selectedItem && (
        <div className="item-details">
          <h3>{selectedItem.name}</h3>
          <p>Category: {selectedItem.category}</p>
          <p>Price: ${selectedItem.price}</p>
          <p>Status: {selectedItem.status}</p>
          <img src={selectedItem.image_url} alt={selectedItem.name} />
        </div>
      )}
    </div>
  );
};

export default TreeView;
