import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles.css'; // Ensure this path is correct

const TreeView = () => {
    const [godowns, setGodowns] = useState([]);
    const [items, setItems] = useState([]);
    const [selectedGodownId, setSelectedGodownId] = useState(null);

    useEffect(() => {
        // Fetch godowns
        axios.get('http://127.0.0.1:8000/api/godowns/')
            .then(response => {
                console.log("Godowns:", response.data); // Debugging
                setGodowns(response.data);
            })
            .catch(error => {
                console.error("Error fetching godowns:", error);
            });

        // Fetch items
        axios.get('http://127.0.0.1:8000/api/items/')
            .then(response => {
                console.log("Items:", response.data); // Debugging
                setItems(response.data);
            })
            .catch(error => {
                console.error("Error fetching items:", error);
            });
    }, []);

    const handleGodownClick = (godownId) => {
        setSelectedGodownId(godownId);
    };

    const renderItems = () => {
        return items
            .filter(item => item.godown === selectedGodownId) // Ensure item.godown matches selectedGodownId
            .map(item => (
                <div key={item.item_id} className="item">
                    <h3>{item.name}</h3>
                    <p>Category: {item.category}</p>
                    <p>Price: ${item.price}</p>
                    <p>Status: {item.status}</p>
                    <img src={item.image_url} alt={item.name} />
                </div>
            ));
    };

    return (
        <div className="tree-view-container">
            <div className="sidebar">
                <h2>Godowns</h2>
                {godowns.map(godown => (
                    <button
                        key={godown.id}
                        className="godown-button"
                        onClick={() => handleGodownClick(godown.id)}
                    >
                        {godown.name}
                    </button>
                ))}
            </div>
            <div className="main-content">
                {selectedGodownId ? (
                    renderItems()
                ) : (
                    <p>Select a godown to see the items.</p>
                )}
            </div>
        </div>
    );
};

export default TreeView;
