// src/components/TreeView.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TreeView = () => {
  const [godowns, setGodowns] = useState([]);  // State for Godowns
  const [selectedItem, setSelectedItem] = useState(null);  // State for selected Item

  // Fetch Godowns on component mount
  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/godowns/')
      .then(response => {
        console.log(response.data);  // Log the fetched data
        setGodowns(response.data);  // Set the Godowns in state
      })
      .catch(error => {
        console.error("There was an error fetching the godowns!", error);
      });
  }, []);

  // Handle click event to fetch item details by Godown ID
  const handleItemClick = (itemId) => {
    axios.get(`http://127.0.0.1:8000/api/items/${itemId}/`)
      .then(response => {
        setSelectedItem(response.data);  // Set the selected item details in state
      })
      .catch(error => {
        console.error("There was an error fetching the item!", error);
      });
  };

  // Recursive function to render Godown tree (for parent-child hierarchy)
  const renderTree = (locations) => {
    return locations.map(location => (
      <li key={location.id} onClick={() => handleItemClick(location.id)}>
        {location.name}
        {location.children && location.children.length > 0 && (
          <ul>
            {renderTree(location.children)}  {/* Recursive call to handle nested Godowns */}
          </ul>
        )}
      </li>
    ));
  };

  return (
    <div className="tree-view-container">
      <h1>Godown Tree View</h1>
      <ul>
        {godowns.length > 0 ? renderTree(godowns) : <li>No locations available.</li>}
      </ul>

      {/* Display selected item details */}
      {selectedItem && (
        <div className="item-details">
          <h3>{selectedItem.name}</h3>
          <p>Category: {selectedItem.category}</p>
          <p>Price: ${selectedItem.price}</p>
          <p>Status: {selectedItem.status}</p>
          <img src={selectedItem.image_url} alt={selectedItem.name} />
        </div>
      )}
    </div>
  );
};

export default TreeView;
