import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles.css'; // Import your CSS file

const TreeView = () => {
    const [godowns, setGodowns] = useState([]);
    const [items, setItems] = useState([]);
    const [expandedGodowns, setExpandedGodowns] = useState({}); // Track which godowns are expanded
    const [selectedItem, setSelectedItem] = useState(null);

    useEffect(() => {
        // Fetch godowns
        axios.get('http://127.0.0.1:8000/api/godowns/')
            .then(response => {
                setGodowns(response.data);
            })
            .catch(error => {
                console.error("There was an error fetching the godowns!", error);
            });
    }, []);

    const handleGodownToggle = (godownId) => {
        if (expandedGodowns[godownId]) {
            // If godown is already expanded, collapse it
            const newExpandedGodowns = { ...expandedGodowns };
            delete newExpandedGodowns[godownId];
            setExpandedGodowns(newExpandedGodowns);
        } else {
            // If godown is not expanded, fetch items and expand it
            axios.get(`http://127.0.0.1:8000/api/items/?godown=${godownId}`)
                .then(response => {
                    setItems(response.data);
                    setExpandedGodowns({ ...expandedGodowns, [godownId]: response.data });
                })
                .catch(error => {
                    console.error("There was an error fetching the items!", error);
                });
        }
    };

    const handleItemClick = (itemId) => {
        // Fetch item details
        axios.get(`http://127.0.0.1:8000/api/items/${itemId}/`)
            .then(response => {
                setSelectedItem(response.data);
            })
            .catch(error => {
                console.error("There was an error fetching the item!", error);
            });
    };

    return (
        <div className="tree-view-container">
            <div className="sidebar">
                <h2>Godowns</h2>
                <ul>
                    {godowns.length > 0 ? (
                        godowns.map(godown => (
                            <li key={godown.id}>
                                <div className="godown-row" onClick={() => handleGodownToggle(godown.id)}>
                                    <span>{godown.name}</span>
                                    <button className="expand-button">
                                        {expandedGodowns[godown.id] ? '-' : '+'}
                                    </button>
                                </div>
                                {expandedGodowns[godown.id] && (
                                    <ul className="item-list">
                                        {expandedGodowns[godown.id].map(item => (
                                            <li key={item.item_id} className="item-button" onClick={() => handleItemClick(item.item_id)}>
                                                {item.name}
                                            </li>
                                        ))}
                                    </ul>
                                )}
                            </li>
                        ))
                    ) : (
                        <li>No locations available.</li>
                    )}
                </ul>
            </div>

            <div className="main-display">
                <h1>Item Details</h1>
                {selectedItem ? (
                    <div className="item-details">
                        <h3>{selectedItem.name}</h3>
                        <p>Category: {selectedItem.category}</p>
                        <p>Price: ${selectedItem.price}</p>
                        <p>Status: {selectedItem.status}</p>
                        <p>Brand: {selectedItem.brand}</p>
                        <img src={selectedItem.image_url} alt={selectedItem.name} />
                    </div>
                ) : (
                    <p>Please select an item to view details.</p>
                )}
            </div>
        </div>
    );
};

export default TreeView;
