// src/components/TreeView.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TreeView = () => {
  const [godowns, setGodowns] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [expandedGodowns, setExpandedGodowns] = useState(new Set());

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/godowns/')
      .then(response => {
        setGodowns(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the godowns!", error);
      });
  }, []);

  const handleItemClick = (itemId) => {
    axios.get(`http://127.0.0.1:8000/api/items/${itemId}/`)
      .then(response => {
        setSelectedItem(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the item!", error);
      });
  };

  const toggleExpandGodown = (id) => {
    const newExpandedGodowns = new Set(expandedGodowns);
    if (newExpandedGodowns.has(id)) {
      newExpandedGodowns.delete(id);
    } else {
      newExpandedGodowns.add(id);
    }
    setExpandedGodowns(newExpandedGodowns);
  };

  const renderTree = (locations) => {
    return locations.map(location => (
      <li key={location.id}>
        <div className="godown-row">
          <span onClick={() => toggleExpandGodown(location.id)}>{location.name}</span>
          <button className="expand-button" onClick={() => toggleExpandGodown(location.id)}>
            {expandedGodowns.has(location.id) ? '-' : '+'}
          </button>
        </div>
        {expandedGodowns.has(location.id) && location.sub_godowns.length > 0 && (
          <ul className="item-list">
            {location.sub_godowns.map(subGodown => (
              <li key={subGodown.id} className="item-button" onClick={() => handleItemClick(subGodown.id)}>
                {subGodown.name}
              </li>
            ))}
          </ul>
        )}
      </li>
    ));
  };

  return (
    <div className="tree-view-container">
      <h1>Godown Tree View</h1>
      <ul>
        {godowns.length > 0 ? renderTree(godowns) : <li>No locations available.</li>}
      </ul>
      {selectedItem && (
        <div className="item-details">
          <h3>{selectedItem.name}</h3>
          <p>Category: {selectedItem.category}</p>
          <p>Price: ${selectedItem.price}</p>
          <p>Status: {selectedItem.status}</p>
          <img src={selectedItem.image_url} alt={selectedItem.name} />
        </div>
      )}
    </div>
  );
};

export default TreeView;
