import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TreeView = () => {
  const [godowns, setGodowns] = useState([]);  // State to store godowns
  const [selectedItem, setSelectedItem] = useState(null);  // State for selected item

  // Fetch godowns on component mount
  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/godowns/')
      .then(response => {
        console.log("Godowns API Response: ", response.data);  // Log API response
        setGodowns(response.data);  // Directly set godowns without hierarchy
      })
      .catch(error => {
        console.error("Error fetching godowns:", error);
      });
  }, []);

  // Fetch item details when clicking on a godown
  const handleItemClick = (itemId) => {
    axios.get(`http://127.0.0.1:8000/api/items/${itemId}/`)
      .then(response => {
        setSelectedItem(response.data);  // Set selected item in state
      })
      .catch(error => {
        console.error("Error fetching item:", error);
      });
  };

  return (
    <div className="tree-view-container">
      <h1>Godown List</h1>
      <ul>
        {/* Render the list of godowns without hierarchy */}
        {godowns.length > 0 ? (
          godowns.map((godown) => (
            <li key={godown.id} onClick={() => handleItemClick(godown.id)}>
              {godown.name}
            </li>
          ))
        ) : (
          <li>No locations available.</li>
        )}
      </ul>

      {/* Display selected item details */}
      {selectedItem && (
        <div className="item-details">
          <h3>{selectedItem.name}</h3>
          <p>Category: {selectedItem.category}</p>
          <p>Price: ${selectedItem.price}</p>
          <p>Status: {selectedItem.status}</p>
          <img src={selectedItem.image_url} alt={selectedItem.name} />
        </div>
      )}
    </div>
  );
};

export default TreeView;
