import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './styles.css'; // Ensure this path is correct

const TreeView = () => {
    const [godowns, setGodowns] = useState([]);
    const [items, setItems] = useState([]);
    const [expandedGodowns, setExpandedGodowns] = useState(new Set());
    const [selectedItem, setSelectedItem] = useState(null);
    const [sidebarVisible, setSidebarVisible] = useState(true); // New state for sidebar visibility

    useEffect(() => {
        // Fetch godowns
        axios.get('http://127.0.0.1:8000/api/godowns/')
            .then(response => {
                console.log("Godowns:", response.data); // Debugging
                setGodowns(response.data);
            })
            .catch(error => {
                console.error("Error fetching godowns:", error);
            });

        // Fetch items
        axios.get('http://127.0.0.1:8000/api/items/')
            .then(response => {
                console.log("Items:", response.data); // Debugging
                setItems(response.data);
            })
            .catch(error => {
                console.error("Error fetching items:", error);
            });
    }, []);

    const handleExpandClick = (godownId) => {
        const newExpandedGodowns = new Set(expandedGodowns);
        if (newExpandedGodowns.has(godownId)) {
            newExpandedGodowns.delete(godownId);
        } else {
            newExpandedGodowns.add(godownId);
        }
        setExpandedGodowns(newExpandedGodowns);
    };

    const renderSubGodowns = (parentId) => {
        return godowns
            .filter(godown => godown.parent_godown === parentId) // Get sub-godowns
            .map(godown => (
                <div key={godown.id} style={{ marginLeft: '20px' }}>
                    <button className="godown-button" onClick={() => handleExpandClick(godown.id)}>
                        {expandedGodowns.has(godown.id) ? '−' : '+'} {godown.name}
                    </button>
                    {expandedGodowns.has(godown.id) && (
                        <div>
                            {renderSubGodowns(godown.id)} {/* Render any further sub-godowns */}
                            {renderItems(godown.id)} {/* Render items associated with this sub-godown */}
                        </div>
                    )}
                </div>
            ));
    };

    const renderItems = (godownId) => {
        return items
            .filter(item => item.godown === godownId) // Ensure item.godown matches godownId
            .map(item => (
                <div key={item.item_id} className="item">
                    <button className="item-button" onClick={() => setSelectedItem(item)}>
                        {item.name}
                    </button>
                </div>
            ));
    };

    return (
        <div className="tree-view-container">
            <button className="toggle-sidebar" onClick={() => setSidebarVisible(!sidebarVisible)}>
                {sidebarVisible ? 'Hide Sidebar' : 'Show Sidebar'}
            </button>
            {sidebarVisible && (
                <div className="sidebar">
                    <h2>Godowns</h2>
                    {godowns.filter(godown => godown.parent_godown === null).map(godown => ( // Only parent godowns
                        <div key={godown.id}>
                            <button className="godown-button" onClick={() => handleExpandClick(godown.id)}>
                                {expandedGodowns.has(godown.id) ? '−' : '+'} {godown.name}
                            </button>
                            {expandedGodowns.has(godown.id) && (
                                <div>
                                    {renderSubGodowns(godown.id)} {/* Render sub-godowns */}
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            )}
            <div className="main-content">
                {selectedItem ? (
                    <div>
                        <h3>{selectedItem.name}</h3>
                        <p>Category: {selectedItem.category}</p>
                        <p>Price: ${selectedItem.price}</p>
                        <p>Status: {selectedItem.status}</p>
                        <img src={selectedItem.image_url} alt={selectedItem.name} />
                    </div>
                ) : (
                    <p>Select a godown to see the items.</p>
                )}
            </div>
        </div>
    );
};

export default TreeView;
