// src/components/TreeView.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './styles.css'; // Make sure to import the CSS file

const TreeView = () => {
  const [godowns, setGodowns] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [expandedGodowns, setExpandedGodowns] = useState(new Set()); // Track expanded godowns

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/godowns/')
      .then(response => {
        console.log(response.data); // Log the response data
        setGodowns(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the godowns!", error);
      });
  }, []);

  const handleItemClick = (itemId) => {
    axios.get(`http://127.0.0.1:8000/api/items/${itemId}/`)
      .then(response => {
        setSelectedItem(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the item!", error);
      });
  };

  const handleGodownClick = (id) => {
    const newExpanded = new Set(expandedGodowns);
    if (newExpanded.has(id)) {
      newExpanded.delete(id); // Collapse if already expanded
    } else {
      newExpanded.add(id); // Expand if not expanded
    }
    setExpandedGodowns(newExpanded);
  };

  const renderTree = (locations) => {
    return locations.map(location => (
      <div key={location.id}>
        <button className="godown-button" onClick={() => handleGodownClick(location.id)}>
          {location.name}
          <span>{expandedGodowns.has(location.id) ? '-' : '+'}</span> {/* Expand/Collapse indicator */}
        </button>
        {expandedGodowns.has(location.id) && location.subGodowns && (
          <div className="subgodowns">
            {location.subGodowns.map(sub => (
              <div key={sub.id}>
                <button className="godown-button" onClick={() => handleGodownClick(sub.id)}>
                  {sub.name}
                  <span>{expandedGodowns.has(sub.id) ? '-' : '+'}</span>
                </button>
                {expandedGodowns.has(sub.id) && sub.items && (
                  <div className="subgodowns">
                    {sub.items.map(item => (
                      <button className="item-button" key={item.item_id} onClick={() => handleItemClick(item.item_id)}>
                        {item.name}
                      </button>
                    ))}
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    ));
  };

  return (
    <div className="tree-view-container">
      <div className="sidebar">
        <h2>Godowns</h2>
        {godowns.length > 0 ? renderTree(godowns) : <p>No locations available.</p>}
      </div>
      <div className="main-content">
        {selectedItem ? (
          <div className="item-details">
            <h3>{selectedItem.name}</h3>
            <p>Quantity: {selectedItem.quantity}</p>
            <p>Category: {selectedItem.category}</p>
            <p>Price: ${selectedItem.price}</p>
            <p>Status: {selectedItem.status}</p>
            <p>Godown: {selectedItem.godown}</p>
            <p>Brand: {selectedItem.brand}</p>
            <p>Type: {selectedItem.attributes?.type}</p>
            <p>Material: {selectedItem.attributes?.material}</p>
            <p>Warranty Years: {selectedItem.attributes?.warranty_years}</p>
            <img src={selectedItem.image_url} alt={selectedItem.name} />
          </div>
        ) : (
          <p>Select an item to see the details.</p>
        )}
      </div>
    </div>
  );
};

export default TreeView;
